import { useContext, useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Nav } from "react-bootstrap";
import styled from "styled-components";
import { cleanup } from "@testing-library/react";

import {Context1} from './../App'
import { useSelector } from "react-redux";

let YellowBtn = styled.button`
  background: ${(props) => props.bg};
  color: ${(props) => (props.bg === "blue" ? "white" : "black")};
  padding: 10px;
`;
// let NewBtn = styled.button(YellowBtn)``

let Box = styled.div`
  background: gray;
  padding: 20px;
`;

export default function Detail(props) {
  let {재고, shoes} = useContext(Context1)

  let { id } = useParams();
  let 찾은상품 = props.shoes.find(function (x) {
    return x.id == id;
  });
  let [alert, setAlert] = useState(true);
  let [ tab, setTab ] = useState(0); // 상태는 3가지 종류가 있으니까..

  useEffect(() => {
    let timer = setTimeout(() => {
      setAlert(false);
    }, 2000);

    // useeffect보다 먼저 실행
    return () => {
      // 기존 타이머 제거
      clearTimeout(timer);
    };
  }, []);

  let [num, setNum] = useState("");

  useEffect(() => {
    if (isNaN(num) == true) {
      alert("그러지 마세요");
    }
  }, [num]);

  let state = useSelector((state) => {return state})

  return (
    <div className="container">
      {/* <YellowBtn bg="blue">버튼</YellowBtn>
      <YellowBtn bg="orange">버튼</YellowBtn> */}

      {alert == true ? <div className="alert alert-warning">2초 이내 구매시 할인</div> : null}

      <div className="row">
        <div className="col-md-6">
          <img src="https://codingapple1.github.io/shop/shoes1.jpg" width="100%" />
        </div>
        <div className="col-md-6">
          <input onChange={(e) => {setNum(e.target.value)}} />
          <h4 className="pt-5">{찾은상품.title}</h4>
          <p>{찾은상품.content}</p>
          <p>{찾은상품.price}원</p>
          <button className="btn btn-danger" onClick={() => {

          }}>주문하기</button>
        </div>
      </div>

    <Nav variant="tabs" defaultActiveKey="/home">
      <Nav.Item>
        <Nav.Link href="/home" onClick={() => { setTab(0)}}>버튼0</Nav.Link>
      </Nav.Item>
      <Nav.Item>
        <Nav.Link eventKey="link-1" onClick={() => { setTab(1)}}>버튼1</Nav.Link>
      </Nav.Item>
      <Nav.Item>
        <Nav.Link eventKey="link-2" onClick={() => { setTab(2)}}>버튼2</Nav.Link>
      </Nav.Item>
    </Nav>
    <TabContent tab={tab}/>


    </div>
  );
}

function TabContent({tab}) {
  // if ( props.tab == 0) {
  //   return <div>내용0</div>
  // } else if ( props.tab == 1) {
  //   return <div>내용1</div>
  // } else if ( props.tab == 2) {
  //   return <div>내용2</div>
  // }

  let [fade, setFade ] = useState('')
  let {재고, shoes} = useContext(Context1) // porps 없이 사용가능...


  useEffect(()=> {
    // timer도 걸어줘야 함, automatic batching 기능 때문에,,, : state 변경이 모두 된 후에 재랜더링..
    let timer = setTimeout(() => {setFade('end')}, 100)

    // css 부착 위해..
    return () => {
      cleanup(timer)
      setFade('')
    }
  }, [tab])

  return (<div className={`start ${fade}`}> {
    [<div>내용0</div>, <div>내용1</div>, <div>내용2</div>][tab]
  }</div>
  )
}
