import { configureStore, createSlice } from "@reduxjs/toolkit";
import { useSelector } from "react-redux";

// useState랑 비슷..
let user = createSlice({
  name: "user",
  initialState: "kim",
});

export default configureStore({
  reducer: {
    // state 등록 필요!
    user: user.reducer,
  },
});
